// components/product.js
import React, { useState } from "react";

const Product = () => {

  const product = {
    name: "Apple",   // 商品名稱
    price: 100,      // 價格
  }  
	return(
		<div>
			<h2>{product.name}</h2>
			<p>${product.price}</p>
            <h3>quantity:</h3>
			<div>
				<button >+</button>
				<button >-</button>
			</div>
			<hr/>
		</div>
	)	
};

export default Product
