import logo from './logo.svg';
import './App.css';
import React from "react";
import Product from "./components/product";

const App = () => {
  // 設定商品資訊
const products = [
  {id: 1, name: "Android", price: 150},
  {id: 2, name: "Apple", price: 170},
  {id: 3, name: "Nokia", price: 65},
];

return(
  <div>
          // 將商品資訊作為參數(Prop)分別傳入元件(Component)
    {products.map( (p) => (
      <Product 
        key={p.id}  
        name={p.name}   
        price={p.price}
        />
    ))}
  </div>
)
};

export default App
